/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground, Props } from 'docz'
import Row from 'antd/es/row';
import BasicDetailsView from 'rk-admin-ui/es/basic-details-view';
import 'rk-admin-ui/es/style/utils.less';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "basicdetailsview"
    }}>{`BasicDetailsView`}</h1>
    <p>{`Display multiple read-only fields in groups.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`columns`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`an object of { xs: 1-24, sm: 1-24, md: 1-24, lg: 1-24, xl: 1-24 }`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`object`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`document`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`an object of data`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`object`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`fields`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`array object of fields label and name, `}<inlineCode parentName="td">{`[{ label: value, name: value}]`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`array object`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onCancel`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Specify a function that will be called when a user clicks close button on top right`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`function(e)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`descriptionField `}{`|`}{` titleField`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string name of object document property`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "basic-usage"
    }}>{`Basic usage`}</h2>
    <Playground __position={0} __code={'<Row>\n  <BasicDetailsView\n    onClose={() => alert(\'Clicked onClose\')}\n    columns={{ xs: 24, sm: 24, md: 24, lg: 24, xl: 24 }}\n    descriptionField=\"name_2\"\n    document={{\n      name: \'Tools & Home Improvement\',\n      name_2: \'Tools & Home Improvement\',\n      is_active: true,\n    }}\n    fields={[\n      {\n        label: \'Name\',\n        name: \'name\',\n      },\n      {\n        label: \'Name 2\',\n        name: \'name_2\',\n      },\n      {\n        label: \'Status\',\n        name: \'is_active\',\n      },\n    ]}\n    titleField=\"name\"\n  />\n</Row>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      Row,
      BasicDetailsView
    }} mdxType="Playground">
    <Row mdxType="Row">
        <BasicDetailsView onClose={() => alert('Clicked onClose')} columns={{
          xs: 24,
          sm: 24,
          md: 24,
          lg: 24,
          xl: 24
        }} descriptionField='name_2' document={{
          name: 'Tools & Home Improvement',
          name_2: 'Tools & Home Improvement',
          is_active: true
        }} fields={[{
          label: 'Name',
          name: 'name'
        }, {
          label: 'Name 2',
          name: 'name_2'
        }, {
          label: 'Status',
          name: 'is_active'
        }]} titleField='name' mdxType="BasicDetailsView" />
    </Row>
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
