/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import NotificationIcon from 'rk-admin-ui/es/notification-icon';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "notificationicon"
    }}>{`NotificationIcon`}</h1>
    <p>{`Display a nuber of unread messages.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`count`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`number to show in badge`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`icon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string name of the ant design icon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`iconTheme`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`theme of the ant design icon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`'filled' `}{`|`}{` 'outlined' `}{`|`}{` 'twoTone'`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`'outlined'`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`twoToneColor`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`only support the two-tone icon. Specific the primary color.`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string (hex color)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
      </tbody>
    </table>
    <blockquote>
      <p parentName="blockquote">{`Note: NotificationIcon accept all `}<a parentName="p" {...{
          "href": "https://ant.design/components/badge/?locale=en-US#API"
        }}>{`Antd Design Badge`}</a>{` properties`}</p>
    </blockquote>
    <h2 {...{
      "id": "sample"
    }}>{`Sample`}</h2>
    <Playground __position={0} __code={'<div className=\"top-notification\">\n  <NotificationIcon key=\"bell\" icon=\"bell\" count=\"9\" />\n</div>'} __scope={{
      props: this ? this.props : props,
      Playground,
      NotificationIcon
    }} mdxType="Playground">
    <div className='top-notification'>
        <NotificationIcon key='bell' icon='bell' count='9' mdxType="NotificationIcon" />
    </div>
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
