/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import Menu from 'antd/es/menu';
import Icon from 'antd/es/icon';
import AppHeader from 'rk-admin-ui/es/app-header';
import NotificationIcon from 'rk-admin-ui/es/notification-icon';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "appheader"
    }}>{`AppHeader`}</h1>
    <p>{`The global header of web app.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`collapsed`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`If `}<inlineCode parentName="td">{`true`}</inlineCode>{`, side menu cannot be opened or closed`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`false`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`menuFoldIcon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of `}<a parentName="td" {...{
              "href": "https://ant.design/components/icon/?locale=en-US#API"
            }}>{`icon`}</a>{` type`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`menu-fold`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`menuUnfoldIcon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of `}<a parentName="td" {...{
              "href": "https://ant.design/components/icon/?locale=en-US#API"
            }}>{`icon`}</a>{` type`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`menu-fold`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`notificationIcons`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><a parentName="td" {...{
              "href": "/notification-icon"
            }}>{`NotificationIcon`}</a>{` properties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onSearch`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`handler of search box`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`func`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`popoverDropdownContent`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Content of the card`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}{`|`}{`ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`popoverDropdownTrigger`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`whether floating PopoverDropdown by `}<inlineCode parentName="td">{`click`}</inlineCode>{` or `}<inlineCode parentName="td">{`hover`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`click`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`searchPlaceholder`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`placeholder of search box`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`Search`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`searchPopoverDropdownTrigger`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`whether floating PopoverDropdown by `}<inlineCode parentName="td">{`click`}</inlineCode>{` or `}<inlineCode parentName="td">{`hover`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`click`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`user`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`object of `}<a parentName="td" {...{
              "href": "/user-avatar"
            }}>{`UserAvartar`}</a>{` properties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`object`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "basic-usage"
    }}>{`Basic usage`}</h2>
    <Playground __position={0} __code={'<AppHeader\n  collapsed={false}\n  isMobile={false}\n  notificationIcons={\n    <>\n      <div className=\"top-notification\">\n        <NotificationIcon\n          key=\"bell\"\n          icon=\"bell\"\n          count=\"5\"\n          iconTheme=\"filled\"\n        />\n      </div>\n      <div className=\"top-notification\">\n        <NotificationIcon\n          key=\"calendar\"\n          icon=\"calendar\"\n          count=\"12\"\n          iconTheme=\"filled\"\n        />\n      </div>\n      <div className=\"top-notification\">\n        <NotificationIcon\n          key=\"setting\"\n          icon=\"setting\"\n          count=\"0\"\n          iconTheme=\"filled\"\n        />\n      </div>\n    </>\n  }\n  onCollapse={() => console.log(\'click on collapse...\')}\n  popoverDropdownContent={\n    <Menu className=\"popover-dropdown-item\">\n      <Menu.Item key=\"new_article\">\n        <Icon type=\"plus\" /> New Invoice\n      </Menu.Item>\n      <Menu.Item key=\"new_category\">\n        <Icon type=\"plus\" /> New Payment\n      </Menu.Item>\n      <Menu.Item key=\"new_tag\">\n        <Icon type=\"plus\" /> New Category\n      </Menu.Item>\n    </Menu>\n  }\n  searchAddon={\n    <Menu\n      className=\"popover-dropdown-item\"\n      selectedKeys={[\'categories\']}\n      onClick={() => console.log(\'clicked search...\')}\n    >\n      <Menu.Item key=\"articles\">Article</Menu.Item>\n      <Menu.Item key=\"categories\">Category</Menu.Item>\n      <Menu.Item key=\"tags\">Tag</Menu.Item>\n    </Menu>\n  }\n  user={{ username: \'Ratana Keo\', shortName: \'RK\', isShowUsername: false }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Menu,
      Icon,
      AppHeader,
      NotificationIcon
    }} mdxType="Playground">
    <AppHeader collapsed={false} isMobile={false} notificationIcons={<>
                <div className='top-notification'>
                    <NotificationIcon key='bell' icon='bell' count='5' iconTheme='filled' mdxType="NotificationIcon" />
                </div>
                <div className='top-notification'>
                    <NotificationIcon key='calendar' icon='calendar' count='12' iconTheme='filled' mdxType="NotificationIcon" />
                </div>
                <div className='top-notification'>
                    <NotificationIcon key='setting' icon='setting' count='0' iconTheme='filled' mdxType="NotificationIcon" />
                </div>
            </>} onCollapse={() => console.log('click on collapse...')} popoverDropdownContent={<Menu className='popover-dropdown-item' mdxType="Menu">
                <Menu.Item key='new_article'>
                    <Icon type='plus' mdxType="Icon" /> New Invoice
                </Menu.Item>
                <Menu.Item key='new_category'>
                    <Icon type='plus' mdxType="Icon" /> New Payment
                </Menu.Item>
                <Menu.Item key='new_tag'>
                    <Icon type='plus' mdxType="Icon" /> New Category
                </Menu.Item>
            </Menu>} searchAddon={<Menu className='popover-dropdown-item' selectedKeys={['categories']} onClick={() => console.log('clicked search...')} mdxType="Menu">
                <Menu.Item key='articles'>Article</Menu.Item>
                <Menu.Item key='categories'>Category</Menu.Item>
                <Menu.Item key='tags'>Tag</Menu.Item>
            </Menu>} user={{
        username: 'Ratana Keo',
        shortName: 'RK',
        isShowUsername: false
      }} mdxType="AppHeader" />
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
