/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import ModalForm from '../components/ModalFormDemo';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "modalform"
    }}>{`ModalForm`}</h1>
    <p>{`When requiring users to interact with the application, but without jumping to a new page and interrupting the user's workflow, you can use Modal to create a new floating layer over the current page to get user feedback or display information.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`columns`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`whether to render input elment with 2 columns or more`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`number`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`1`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`modalProps`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`accept all `}<a parentName="td" {...{
              "href": "https://ant.design/components/modal/?locale=en-US#API"
            }}>{`Antd Design Modal`}</a>{` properties`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`object`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`fields`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`array object of `}<a parentName="td" {...{
              "href": "/dynamic-element#fields-properties"
            }}>{`fields properties`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`array object`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "sample"
    }}>{`Sample`}</h2>
    <Playground __position={0} __code={'<ModalForm\n  fields={[\n    {\n      isMultiFields: true,\n      fields: [\n        {\n          formItem: {\n            label: \'Name\',\n            key: \'name\',\n            colon: false,\n            style: {\n              display: \'inline-block\',\n              width: \'calc(50% - 12px)\',\n              marginTop: 8,\n            },\n          },\n          decorator: {\n            rules: [{ required: true, message: \'Please enter name\' }],\n          },\n          element: {\n            placeholder: \'Name\',\n          },\n          type: \'input\',\n          name: \'name\',\n        },\n        {\n          formItem: {\n            label: \'Name 1\',\n            key: \'name_1\',\n            colon: false,\n            style: {\n              display: \'inline-block\',\n              width: \'calc(50% - 12px)\',\n              marginTop: 8,\n              marginLeft: 24,\n            },\n          },\n          decorator: {},\n          element: {\n            placeholder: \'Name 1\',\n          },\n          type: \'input\',\n          name: \'name_1\',\n        },\n      ],\n      formItem: {\n        className: \'mb-0 p-0\',\n      },\n    },\n    {\n      formItem: {\n        label: \'Name 2\',\n        key: \'name_2\',\n        colon: false,\n      },\n      decorator: {},\n      element: {\n        placeholder: \'Name 2\',\n      },\n      type: \'input\',\n      name: \'name_2\',\n    },\n    {\n      formItem: {\n        label: \'Status\',\n        key: \'is_active\',\n        colon: false,\n      },\n      decorator: {\n        rules: [{ required: false }],\n        valuePropName: \'checked\',\n        initialValue: true,\n      },\n      element: {},\n      type: \'switch\',\n      name: \'is_active\',\n    },\n  ]}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      ModalForm
    }} mdxType="Playground">
   <ModalForm fields={[{
        isMultiFields: true,
        fields: [{
          formItem: {
            label: 'Name',
            key: 'name',
            colon: false,
            style: {
              display: 'inline-block',
              width: 'calc(50% - 12px)',
              marginTop: 8
            }
          },
          decorator: {
            rules: [{
              required: true,
              message: 'Please enter name'
            }]
          },
          element: {
            placeholder: 'Name'
          },
          type: 'input',
          name: 'name'
        }, {
          formItem: {
            label: 'Name 1',
            key: 'name_1',
            colon: false,
            style: {
              display: 'inline-block',
              width: 'calc(50% - 12px)',
              marginTop: 8,
              marginLeft: 24
            }
          },
          decorator: {},
          element: {
            placeholder: 'Name 1'
          },
          type: 'input',
          name: 'name_1'
        }],
        formItem: {
          className: 'mb-0 p-0'
        }
      }, {
        formItem: {
          label: 'Name 2',
          key: 'name_2',
          colon: false
        },
        decorator: {},
        element: {
          placeholder: 'Name 2'
        },
        type: 'input',
        name: 'name_2'
      }, {
        formItem: {
          label: 'Status',
          key: 'is_active',
          colon: false
        },
        decorator: {
          rules: [{
            required: false
          }],
          valuePropName: 'checked',
          initialValue: true
        },
        element: {},
        type: 'switch',
        name: 'is_active'
      }]} mdxType="ModalForm" />
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
