/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import UserAvatar from 'rk-admin-ui/es/user-avatar';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "useravatar"
    }}>{`UserAvatar`}</h1>
    <p>{`UserAvatar can be used to represent people or objects. It supports images or letters.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`isShowUsername`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`handle wheather show username or not`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`photo`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`the url of the image for an UseAvatar`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`shape`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`the shape of avatar`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`circle`}</inlineCode>{` `}{`|`}{` `}<inlineCode parentName="td">{`square`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`circle`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`shortName`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of letters`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`username`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of name`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "sample"
    }}>{`Sample`}</h2>
    <Playground __position={0} __code={'<UserAvatar isShowUsername={true} username=\"Ratana Keo\" shortName=\"RK\" />'} __scope={{
      props: this ? this.props : props,
      Playground,
      UserAvatar
    }} mdxType="Playground">
    <UserAvatar isShowUsername={true} username='Ratana Keo' shortName='RK' mdxType="UserAvatar" />
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
