/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import UserAvatar from 'rk-admin-ui/es/user-avatar';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "useravatar"
    }}>{`UserAvatar`}</h1>
    <p>{`UserAvatar can be used to represent people or objects. It supports images or letters.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`isShowUsername`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`wheather show username`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`false`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`shortName`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of letters`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`username`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of name`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`type`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`can be set to default, primary, success, warning, danger`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`primary`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <blockquote>
      <p parentName="blockquote">{`Note: Accept all `}<a parentName="p" {...{
          "href": "https://ant.design/components/avatar/?locale=en-US#API"
        }}>{`Avatar Properties`}</a></p>
    </blockquote>
    <h2 {...{
      "id": "basic-usage"
    }}>{`Basic Usage`}</h2>
    <Playground __position={0} __code={'<UserAvatar\n  isShowUsername={false}\n  username=\"Ratana Keo\"\n  shortName=\"RK\"\n  type=\"default\"\n  style={{ marginRight: 8 }}\n/>\n<UserAvatar\n  isShowUsername={false}\n  username=\"Ratana Keo\"\n  shortName=\"RK\"\n  style={{ marginRight: 8 }}\n/>\n<UserAvatar\n  isShowUsername={false}\n  username=\"Ratana Keo\"\n  shortName=\"RK\"\n  type=\"success\"\n  style={{ marginRight: 8 }}\n/>\n<UserAvatar\n  isShowUsername={false}\n  username=\"Ratana Keo\"\n  shortName=\"RK\"\n  type=\"warning\"\n  style={{ marginRight: 8 }}\n/>\n<UserAvatar\n  isShowUsername={false}\n  username=\"Ratana Keo\"\n  shortName=\"RK\"\n  type=\"danger\"\n  style={{ marginRight: 8 }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      UserAvatar
    }} mdxType="Playground">
    <UserAvatar isShowUsername={false} username='Ratana Keo' shortName='RK' type='default' style={{
        marginRight: 8
      }} mdxType="UserAvatar" />
    <UserAvatar isShowUsername={false} username='Ratana Keo' shortName='RK' style={{
        marginRight: 8
      }} mdxType="UserAvatar" />
    <UserAvatar isShowUsername={false} username='Ratana Keo' shortName='RK' type='success' style={{
        marginRight: 8
      }} mdxType="UserAvatar" />
    <UserAvatar isShowUsername={false} username='Ratana Keo' shortName='RK' type='warning' style={{
        marginRight: 8
      }} mdxType="UserAvatar" />
    <UserAvatar isShowUsername={false} username='Ratana Keo' shortName='RK' type='danger' style={{
        marginRight: 8
      }} mdxType="UserAvatar" />
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
