/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import EditableTable from '../components/EditableTableDemo';
import Button from 'antd/lib/button';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "editabletable"
    }}>{`EditableTable`}</h1>
    <p>{`A table displays rows of data.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`columns`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`array object of `}<a parentName="td" {...{
              "href": "/editable-table#columns-properties"
            }}>{`columns properties`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`editable`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`whether table row is able to edit`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`false`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onFieldChange`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`hanlder of column element blur`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`function(form, name, record, index)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "columns-properties"
    }}>{`columns Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`fields`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`array object of `}<a parentName="td" {...{
              "href": "/dynamic-element#fields-properties"
            }}>{`DynamicElement Properties`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`column`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`table properties, `}<a parentName="td" {...{
              "href": "https://ant.design/components/table/#API"
            }}>{`read details`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`false`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "sample"
    }}>{`Sample`}</h2>
    <Playground __position={0} __code={'<EditableTable\n  editable={true}\n  columns={[\n    {\n      field: {\n        formItem: { key: \'name\' },\n        decorator: {\n          rules: [{ required: true, message: <></> }],\n        },\n        element: {\n          placeholder: \'Name\',\n          readOnly: true,\n          className: \'editable-table-element\',\n        },\n        type: \'input\',\n        name: \'name\',\n        bindEventOnFieldChanged: false,\n      },\n      column: {\n        title: \'Name\',\n        dataIndex: \'name\',\n        width: \'25%\',\n      },\n    },\n    {\n      field: {\n        formItem: { key: \'uom_id\' },\n        decorator: { rules: [{ required: true, message: <></> }] },\n        element: {\n          placeholder: \'Unit of measure\',\n          showSearch: true,\n          optionFilterProp: \'children\',\n          className: \'editable-table-element w-100-per\',\n        },\n        type: \'select\',\n        name: \'uom_id\',\n        bindEventOnFieldChanged: true,\n        data: [{ _id: 1, name: \'Can\' }, { _id: 2, name: \'Unit\' }],\n        display_value: \'_id\',\n        display_name: \'name\',\n      },\n      column: {\n        title: \'Unit of measure\',\n        dataIndex: \'uom_id\',\n        width: \'20%\',\n      },\n    },\n    {\n      field: {\n        formItem: {\n          key: \'quantity\',\n        },\n        decorator: {\n          rules: [{ required: true, message: <></> }],\n          initialValue: 1,\n        },\n        element: {\n          placeholder: \'Quantity\',\n          className: \'editable-table-element w-100-per\',\n        },\n        type: \'number\',\n        name: \'quantity\',\n        bindEventOnFieldChanged: true,\n      },\n      column: {\n        title: \'Quantity\',\n        dataIndex: \'quantity\',\n        width: \'15%\',\n      },\n    },\n    {\n      field: {\n        formItem: {\n          key: \'price\',\n        },\n        decorator: { rules: [{ required: true, message: <></> }] },\n        element: {\n          placeholder: \'Price\',\n          className: \'editable-table-element w-100-per\',\n        },\n        type: \'number\',\n        name: \'price\',\n        bindEventOnFieldChanged: true,\n      },\n      column: {\n        title: \'Price\',\n        dataIndex: \'price\',\n      },\n    },\n    {\n      field: {\n        formItem: {\n          key: \'amount\',\n        },\n        decorator: { rules: [] },\n        element: {\n          placeholder: \'Amount\',\n          readOnly: true,\n          className: \'editable-table-element w-100-per\',\n        },\n        type: \'number\',\n        name: \'amount\',\n        bindEventOnFieldChanged: false,\n      },\n      column: {\n        title: \'Amount\',\n        dataIndex: \'amount\',\n      },\n    },\n    {\n      field: {},\n      column: {},\n      isCustomRender: true,\n      render: (text, record, index) => (\n        <div className=\"editable-table-action\">\n          <Button\n            type=\"danger\"\n            ghost\n            shape=\"circle\"\n            size=\"small\"\n            onClick={() => alert(\'You clicked remove\')}\n            icon=\"minus\"\n          />\n        </div>\n      ),\n    },\n  ]}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      EditableTable,
      Button
    }} mdxType="Playground">
    <EditableTable editable={true} columns={[{
        field: {
          formItem: {
            key: 'name'
          },
          decorator: {
            rules: [{
              required: true,
              message: <></>
            }]
          },
          element: {
            placeholder: 'Name',
            readOnly: true,
            className: 'editable-table-element'
          },
          type: 'input',
          name: 'name',
          bindEventOnFieldChanged: false
        },
        column: {
          title: 'Name',
          dataIndex: 'name',
          width: '25%'
        }
      }, {
        field: {
          formItem: {
            key: 'uom_id'
          },
          decorator: {
            rules: [{
              required: true,
              message: <></>
            }]
          },
          element: {
            placeholder: 'Unit of measure',
            showSearch: true,
            optionFilterProp: 'children',
            className: 'editable-table-element w-100-per'
          },
          type: 'select',
          name: 'uom_id',
          bindEventOnFieldChanged: true,
          data: [{
            _id: 1,
            name: 'Can'
          }, {
            _id: 2,
            name: 'Unit'
          }],
          display_value: '_id',
          display_name: 'name'
        },
        column: {
          title: 'Unit of measure',
          dataIndex: 'uom_id',
          width: '20%'
        }
      }, {
        field: {
          formItem: {
            key: 'quantity'
          },
          decorator: {
            rules: [{
              required: true,
              message: <></>
            }],
            initialValue: 1
          },
          element: {
            placeholder: 'Quantity',
            className: 'editable-table-element w-100-per'
          },
          type: 'number',
          name: 'quantity',
          bindEventOnFieldChanged: true
        },
        column: {
          title: 'Quantity',
          dataIndex: 'quantity',
          width: '15%'
        }
      }, {
        field: {
          formItem: {
            key: 'price'
          },
          decorator: {
            rules: [{
              required: true,
              message: <></>
            }]
          },
          element: {
            placeholder: 'Price',
            className: 'editable-table-element w-100-per'
          },
          type: 'number',
          name: 'price',
          bindEventOnFieldChanged: true
        },
        column: {
          title: 'Price',
          dataIndex: 'price'
        }
      }, {
        field: {
          formItem: {
            key: 'amount'
          },
          decorator: {
            rules: []
          },
          element: {
            placeholder: 'Amount',
            readOnly: true,
            className: 'editable-table-element w-100-per'
          },
          type: 'number',
          name: 'amount',
          bindEventOnFieldChanged: false
        },
        column: {
          title: 'Amount',
          dataIndex: 'amount'
        }
      }, {
        field: {},
        column: {},
        isCustomRender: true,
        render: (text, record, index) => <div className='editable-table-action'>
                        <Button type='danger' ghost shape='circle' size='small' onClick={() => alert('You clicked remove')} icon='minus' mdxType="Button" />
                    </div>
      }]} mdxType="EditableTable" />
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
