/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground, Props  } from 'docz'
import ButtonTooltip from 'rk-admin-ui/es/button-tooltip';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "buttontooltip"
    }}>{`ButtonTooltip`}</h1>
    <p>{`To trigger an operation and show tooltip when hover.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <p>{`To get a customized button, just set `}<inlineCode parentName="p">{`type`}</inlineCode>{`/`}<inlineCode parentName="p">{`shape`}</inlineCode>{`/`}<inlineCode parentName="p">{`size`}</inlineCode>{`/`}<inlineCode parentName="p">{`loading`}</inlineCode>{`/`}<inlineCode parentName="p">{`disabled`}</inlineCode>{`.`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`title`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The text shown in the tooltip`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}{`|`}{`ReactNode`}{`|`}{`() => ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`placement`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The position of the tooltip relative to the target, which can be one of `}<inlineCode parentName="td">{`top`}</inlineCode>{` `}<inlineCode parentName="td">{`left`}</inlineCode>{` `}<inlineCode parentName="td">{`right`}</inlineCode>{` `}<inlineCode parentName="td">{`bottom`}</inlineCode>{` `}<inlineCode parentName="td">{`topLeft`}</inlineCode>{` `}<inlineCode parentName="td">{`topRight`}</inlineCode>{` `}<inlineCode parentName="td">{`bottomLeft`}</inlineCode>{` `}<inlineCode parentName="td">{`bottomRight`}</inlineCode>{` `}<inlineCode parentName="td">{`leftTop`}</inlineCode>{` `}<inlineCode parentName="td">{`leftBottom`}</inlineCode>{` `}<inlineCode parentName="td">{`rightTop`}</inlineCode>{` `}<inlineCode parentName="td">{`rightBottom`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`top`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`disabled`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`disabled state of button`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`false`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`ghost`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`make background transparent and invert text and border colors`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`href`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`redirect url of link button`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`htmlType`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`set the original html `}<inlineCode parentName="td">{`type`}</inlineCode>{` of `}<inlineCode parentName="td">{`button`}</inlineCode>{`, see: `}<a parentName="td" {...{
              "href": "https://developer.mozilla.org/en-US/docs/Web/HTML/Element/button#attr-type"
            }}>{`MDN`}</a></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`button`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`icon`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`set the icon of button, see: Icon component`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`loading`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`set the loading status of button`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean `}{`|`}{` { delay: number }`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`false`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`shape`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`can be set to `}<inlineCode parentName="td">{`circle`}</inlineCode>{`, `}<inlineCode parentName="td">{`round`}</inlineCode>{` or omitted`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`size`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`can be set to `}<inlineCode parentName="td">{`small`}</inlineCode>{` `}<inlineCode parentName="td">{`large`}</inlineCode>{` or omitted`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`default`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`target`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`same as target attribute of a, works when href is specified`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`type`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`can be set to `}<inlineCode parentName="td">{`primary`}</inlineCode>{` `}<inlineCode parentName="td">{`ghost`}</inlineCode>{` `}<inlineCode parentName="td">{`dashed`}</inlineCode>{` `}<inlineCode parentName="td">{`danger`}</inlineCode>{` or omitted (meaning `}<inlineCode parentName="td">{`default`}</inlineCode>{`)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`default`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`onClick`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`set the handler to handle `}<inlineCode parentName="td">{`click`}</inlineCode>{` event`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`(event) => void`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`block`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`option to fit button width to its parent width`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`boolean`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`false`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "sample"
    }}>{`Sample`}</h2>
    <Playground __position={0} __code={'<ButtonTooltip title=\"Primary\" type=\"primary\" style={{ marginRight: 16 }}>\n  Primary\n</ButtonTooltip>\n<ButtonTooltip title=\"Default\" style={{ marginRight: 16 }}>\n  Default\n</ButtonTooltip>\n<ButtonTooltip title=\"Danger\" type=\"danger\" style={{ marginRight: 16 }}>\n  Danger\n</ButtonTooltip>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      ButtonTooltip
    }} mdxType="Playground">
  <ButtonTooltip title='Primary' type='primary' style={{
        marginRight: 16
      }} mdxType="ButtonTooltip">Primary</ButtonTooltip>
  <ButtonTooltip title='Default' style={{
        marginRight: 16
      }} mdxType="ButtonTooltip">Default</ButtonTooltip>
  <ButtonTooltip title='Danger' type='danger' style={{
        marginRight: 16
      }} mdxType="ButtonTooltip">Danger</ButtonTooltip>
    </Playground>
    <h2 {...{
      "id": "more"
    }}>{`More`}</h2>
    <Playground __position={1} __code={'<ButtonTooltip\n  title=\"Circle\"\n  type=\"primary\"\n  shape=\"circle\"\n  icon=\"plus\"\n  style={{ marginRight: 16 }}\n/>\n<ButtonTooltip\n  title=\"Round\"\n  shape=\"round\"\n  type=\"primary\"\n  icon=\"search\"\n  style={{ marginRight: 16 }}\n>\n  Search\n</ButtonTooltip>\n<ButtonTooltip\n  title=\"Loading\"\n  shape=\"round\"\n  type=\"primary\"\n  icon=\"search\"\n  loading\n  style={{ marginRight: 16 }}\n>\n  Loading\n</ButtonTooltip>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Props,
      ButtonTooltip
    }} mdxType="Playground">
  <ButtonTooltip title='Circle' type='primary' shape="circle" icon="plus" style={{
        marginRight: 16
      }} mdxType="ButtonTooltip" />
  <ButtonTooltip title='Round' shape="round" type='primary' icon="search" style={{
        marginRight: 16
      }} mdxType="ButtonTooltip">Search</ButtonTooltip>
  <ButtonTooltip title='Loading' shape="round" type='primary' icon="search" loading style={{
        marginRight: 16
      }} mdxType="ButtonTooltip">Loading</ButtonTooltip>
    </Playground>
    <blockquote>
      <p parentName="blockquote">{`Note: ButtonTooltip not working with Button.Group component`}</p>
    </blockquote>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
