/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground} from 'docz'
import Row from 'antd/es/row';
import BasicTable from 'rk-admin-ui/es/basic-table';
import Tag from 'rk-admin-ui/es/preset-tag';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "basictable"
    }}>{`BasicTable`}</h1>
    <p>{`A table displays rows of data.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <blockquote>
      <p parentName="blockquote">{`Note: Accept all `}<a parentName="p" {...{
          "href": "https://ant.design/components/table/?locale=en-US#API"
        }}>{`Table Properties`}</a></p>
    </blockquote>
    <h2 {...{
      "id": "usage"
    }}>{`Usage`}</h2>
    <Playground __position={0} __code={'<BasicTable\n  columns={[\n    {\n      title: \'Name\',\n      dataIndex: \'name\',\n      key: \'name\',\n    },\n    {\n      title: \'Name 2\',\n      dataIndex: \'name_2\',\n      key: \'name_2\',\n    },\n    {\n      title: \'Status\',\n      key: \'status\',\n      dataIndex: \'is_active\',\n      render: (text, record) => (\n        <Tag type={record.is_active === true ? \'success\' : \'danger\'}>\n          {record.is_active === true ? \'Active\' : \'Inactive\'}\n        </Tag>\n      ),\n    },\n  ]}\n  dataSource={[\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Electronics\',\n      name_2: \'Electronics\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Music\',\n      name_2: \'Music\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Women Fashion\',\n      name_2: \'Women Fashion\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Men Fashion\',\n      name_2: \'Men Fashion\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Movies & Television\',\n      name_2: \'Movies & Television\',\n      is_active: false,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Software\',\n      name_2: \'Software\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Sports and Outdoors\',\n      name_2: \'Sports and Outdoors\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Tools & Home Improvement\',\n      name_2: \'Tools & Home Improvement\',\n      is_active: true,\n    },\n    {\n      _id: Math.floor(Math.random() * 1000000000),\n      name: \'Baby\',\n      name_2: \'Baby\',\n      is_active: true,\n    },\n  ]}\n  pagination={{\n    hideOnSinglePage: true,\n    pageSize: 5,\n    total: 9,\n    showTotal: (total, range) => `${range[0]}-${range[1]} of ${total}`,\n  }}\n  rowKey=\"_id\"\n  rowSelection={{\n    onChange: (selectedRowKeys, selectedRows) => {},\n  }}\n/>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Row,
      BasicTable,
      Tag
    }} mdxType="Playground">
    <BasicTable columns={[{
        title: 'Name',
        dataIndex: 'name',
        key: 'name'
      }, {
        title: 'Name 2',
        dataIndex: 'name_2',
        key: 'name_2'
      }, {
        title: 'Status',
        key: 'status',
        dataIndex: 'is_active',
        render: (text, record) => <Tag type={record.is_active === true ? 'success' : 'danger'} mdxType="Tag">
                            {record.is_active === true ? 'Active' : 'Inactive'}
                        </Tag>
      }]} dataSource={[{
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Electronics',
        name_2: 'Electronics',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Music',
        name_2: 'Music',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Women Fashion',
        name_2: 'Women Fashion',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Men Fashion',
        name_2: 'Men Fashion',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Movies & Television',
        name_2: 'Movies & Television',
        is_active: false
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Software',
        name_2: 'Software',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Sports and Outdoors',
        name_2: 'Sports and Outdoors',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Tools & Home Improvement',
        name_2: 'Tools & Home Improvement',
        is_active: true
      }, {
        _id: Math.floor(Math.random() * 1000000000),
        name: 'Baby',
        name_2: 'Baby',
        is_active: true
      }]} pagination={{
        hideOnSinglePage: true,
        pageSize: 5,
        total: 9,
        showTotal: (total, range) => `${range[0]}-${range[1]} of ${total}`
      }} rowKey='_id' rowSelection={{
        onChange: (selectedRowKeys, selectedRows) => {}
      }} mdxType="BasicTable" />
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
