/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground } from 'docz'
import Menu from 'antd/lib/menu';
import '../../node_modules/antd/lib/menu/style/index.css';
import Icon from 'antd/lib/icon';
import PopoverDropdown from 'rk-admin-ui/es/popover-dropdown';

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "popoverdropdown"
    }}>{`PopoverDropdown`}</h1>
    <p>{`The floating dow list popped by clicking or hovering.`}</p>
    <h2 {...{
      "id": "properties"
    }}>{`Properties`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Property`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Type`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Default`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`className`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string of className`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`no-arrow`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`content`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`content of the card`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}{`|`}{`ReactNode`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`-`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`placement`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`The position of the PopoverDropdown relative to the target, which can be one of `}<inlineCode parentName="td">{`top`}</inlineCode>{` `}<inlineCode parentName="td">{`left`}</inlineCode>{` `}<inlineCode parentName="td">{`right`}</inlineCode>{` `}<inlineCode parentName="td">{`bottom`}</inlineCode>{` `}<inlineCode parentName="td">{`topLeft`}</inlineCode>{` `}<inlineCode parentName="td">{`topRight`}</inlineCode>{` `}<inlineCode parentName="td">{`bottomLeft`}</inlineCode>{` `}<inlineCode parentName="td">{`bottomRight`}</inlineCode>{` `}<inlineCode parentName="td">{`leftTop`}</inlineCode>{` `}<inlineCode parentName="td">{`leftBottom`}</inlineCode>{` `}<inlineCode parentName="td">{`rightTop`}</inlineCode>{` `}<inlineCode parentName="td">{`rightBottom`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`bottomLeft`}</inlineCode></td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`trigger`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`whether floating PopoverDropdown by `}<inlineCode parentName="td">{`click`}</inlineCode>{` or `}<inlineCode parentName="td">{`hover`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`string`}</td>
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`hover`}</inlineCode></td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "basic-usage"
    }}>{`Basic usage`}</h2>
    <Playground __position={0} __code={'<PopoverDropdown\n  content={\n    <Menu className=\"popover-dropdown-item\">\n      <Menu.Item key=\"new_article\">\n        <Icon type=\"plus\" /> New Article\n      </Menu.Item>\n      <Menu.Item key=\"new_category\">\n        <Icon type=\"plus\" /> New Category\n      </Menu.Item>\n      <Menu.Item key=\"new_tag\">\n        <Icon type=\"plus\" /> New Tag\n      </Menu.Item>\n    </Menu>\n  }\n>\n  Hover Me\n  <Icon\n    type=\"caret-down\"\n    theme=\"filled\"\n    className=\"page-header-icon-dropdown\"\n  />\n</PopoverDropdown>'} __scope={{
      props: this ? this.props : props,
      Playground,
      Menu,
      Icon,
      PopoverDropdown
    }} mdxType="Playground">
    <PopoverDropdown content={<Menu className='popover-dropdown-item' mdxType="Menu">
            <Menu.Item key='new_article'>
                <Icon type='plus' mdxType="Icon" /> New Article
            </Menu.Item>
            <Menu.Item key='new_category'>
                <Icon type='plus' mdxType="Icon" /> New Category
            </Menu.Item>
            <Menu.Item key='new_tag'>
                <Icon type='plus' mdxType="Icon" /> New Tag
            </Menu.Item>
        </Menu>} mdxType="PopoverDropdown">
        Hover Me 
        <Icon type='caret-down' theme='filled' className='page-header-icon-dropdown' mdxType="Icon" />
    </PopoverDropdown>
    </Playground>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
